import jwt from "jsonwebtoken";
export default (function (token) {
  if (token) {
    var data = jwt.decode(token);

    if (!Boolean(data)) {
      return false;
    }

    var expDate = new Date(data.exp * 1000);
    var currentDate = new Date();
    return expDate - currentDate > 0;
  }

  return false;
});
