import React from "react";
import Link from "next/link";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons/faBars";
import { faShoppingBag } from "@fortawesome/free-solid-svg-icons/faShoppingBag";
import { useAuth } from "../contexts/AuthContext";
import { useRouter } from "next/router";
import { useCartState } from "../contexts/CartContext";

const Nav = () => {
  const { authenticated, logout } = useAuth();
  const { items } = useCartState();
  const router = useRouter();

  const onLogoutClick = () => {
    logout();
    router.push("/login");
  };
  return (
    <nav className="app-navbar">
      <div className="container">
        <div className="nav-left">
          <div className="logo">
            <Link href={"/"}>
              {/*<Image src={'/images/logo.png'} height={"3rem"} width={"auto"}/>*/}
              <h4>Creation Arts</h4>
            </Link>
          </div>
        </div>

        <div className="nav-container">
          <div className="nav-item">
            <Link href={"/"}>Home</Link>
          </div>
          <div className="nav-item">
            <Link href={"/products"}>Products</Link>
          </div>
          <div className="nav-item">
            <Link href={"/contact"}>Contact</Link>
          </div>
        </div>

        <div className="nav-right">
          <div className="nav-item">
            {authenticated ? (
              <span onClick={onLogoutClick}>Logout</span>
            ) : (
              <Link href={"/login"}>Login</Link>
            )}{" "}
          </div>
          <div className="nav-item nav-cart-item">
            {items.length > 0 && (
              <span className="badge accent"> {items.length} </span>
            )}
            <Link href={"/cart"}>
              <FontAwesomeIcon icon={faShoppingBag} size={"2x"} />
            </Link>
          </div>
          <div className="nav-item mobile-nav">
            <FontAwesomeIcon icon={faBars} size={"2x"} />
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Nav;
