import React, { Fragment } from "react";
import Head from "next/head";
import Nav from "../../components/nav";
import Footer from "../../components/footer";
import CartItem from "../../components/cartItem";
import { useCartActions, useCartState } from "../../contexts/CartContext";
import { useRouter } from "next/router";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons/faArrowLeft";
import { faShoppingBag } from "@fortawesome/free-solid-svg-icons/faShoppingBag";

const Cart = () => {
  const cart = useCartState();
  const { countItem } = useCartActions();
  const router = useRouter();

  if (countItem() === 0) {
    return (
      <Fragment>
        <Head>
          <title>Cart</title>
          <link rel="icon" href="/favicon.ico" />
        </Head>
        <Nav />
        <section className="container">
          <div className="app-page-container">
            <div className="info-container">
              <div className="empty-cart"></div>
              <h3 className="mb-2 text-center">Shopping Cart</h3>
              <div className="text-center" style={{ fontSize: "1.125rem" }}>
                No items added to the cart.
              </div>
            </div>
          </div>
        </section>
      </Fragment>
    );
  }

  return (
    <Fragment>
      <Head>
        <title>Products</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Nav />
      <section className="container">
        <div className="app-page-container">
          <h4 className="mb-2">Shopping Cart</h4>
          <div className="cart-item-container">
            <div className="cart-item-header">
              <div className="product-name">Product</div>
              <div className="product-price">Product Price</div>
              <div className="product-qty">Quantity</div>
            </div>
            {cart.items.map((item) => (
              <CartItem data={item} />
            ))}
          </div>

          <div className="checkout-container">
            <button
              className="btn primary-outline-btn checkout-btn"
              onClick={() => router.push("/products")}
            >
              <FontAwesomeIcon icon={faArrowLeft} size={"2x"} />
              Back to shopping
            </button>
            <button
              className="btn accent-btn checkout-btn"
              onClick={() => router.push("/checkout")}
            >
              <FontAwesomeIcon icon={faShoppingBag} size={"2x"} />
              Checkout
            </button>
          </div>
        </div>
      </section>
      <Footer />
    </Fragment>
  );
};

export default Cart;
